  /**
   *  Updates the object in place by only merging the set
   *  properties of object.
   *
   *  This method will create an immutable object with the given ID if
   *  it does not already exist.
   *
   *  This is a "private" method called when data is received from the
   *  server and should not be used by others.
   *
   *  @pre object.id must be a valid object ID
   *  @return an Immutable constructed from object and deep merged with the current state
   */
  _updateObject(object, notify_subscribers = false, emit = true) {
    if (!('id' in object)) {
      console.log('object with no id:', object);

      if ('balance' in object && 'owner' in object && 'settlement_date' in object) {
        // Settle order object
        emitter.emit('settle-order-update', object);
      }

      return;
    }

    let objectSpace = object.id.split('.').slice(0, -1);
    objectSpace.push(null); // Push an empty element into the array to take up the id space.
    objectSpace = objectSpace.join('.');

    /*
    * A lot of objects get spammed by the API that we don't care about, filter these out here
    */
    // Transaction object
    if (objectSpace === transaction_prefix) {
      return; // console.log("not interested in transaction:", object);
    }

    if (objectSpace === account_transaction_history_prefix) {
      // transaction_history object
      if (!this._isSubbedTo('accounts', object.account)) {
        return; // console.log("not interested in transaction_history of", object.account);
      }
    } else if (objectSpace === order_prefix) {
      // limit_order object
      if (!this._isSubbedTo('accounts', object.seller)) {
        return; // console.log("not interested in limit_orders of", object.seller);
      }
    } else if (objectSpace === call_order_prefix) {
      // call_order object
      if (!this._isSubbedTo('accounts', object.borrower)) {
        return; // console.log("not interested in call_orders of", object.borrower);
      }
    } else if (objectSpace === balance_prefix) {
      // balance object
      if (!this._isSubbedTo('accounts', object.owner)) {
        return; // console.log("not interested in balance_object of", object.owner);
      }
    } else if (objectSpace === operation_history_prefix) {
      // operation_history object
      return; // console.log("not interested in operation_history", object);
    } else if (objectSpace === block_summary_prefix) {
      // block_summary object
      return; // console.log("not interested in block_summary_prefix", object);
    } else if (objectSpace === account_stats_prefix) {
      // account_stats object
      if (!this._isSubbedTo('accounts', object.owner)) {
        return; // console.log("not interested in stats of", object.owner);
      }
    } else if (objectSpace === witness_prefix) {
      // witness object
      if (!this._isSubbedTo('witnesses', object.id)) {
        return;
      }
    } else if (objectSpace === committee_prefix) {
      // committee_member object
      if (!this._isSubbedTo('committee', object.id)) {
        return;
      }
    }

    // DYNAMIC GLOBAL OBJECT
    if (object.id === '2.1.0') {
      object.participation = 100 * (BigInteger(object.recent_slots_filled).bitCount() / 128.0);
      this.head_block_time_string = object.time;
      this.chain_time_offset.push(Date.now() - ChainStore.timeStringToDate(object.time).getTime());

      if (this.chain_time_offset.length > 10) {
        this.chain_time_offset.shift(); // remove first
      }

      this.fetchRecentOperations(object.head_block_number);
    }

    let current = this.objects_by_id.get(object.id, undefined);

    if (current === undefined || current === true) {
      current = Immutable.Map();
    }

    let prior = current;

    if (current === undefined || current === true) {
      this.objects_by_id = this.objects_by_id.set(object.id, (current = Immutable.fromJS(object)));
    } else {
      this.objects_by_id = this.objects_by_id.set(
        object.id,
        (current = current.mergeDeep(Immutable.fromJS(object)))
      );
    }

    // BALANCE OBJECT
    if (objectSpace === balance_prefix) {
      let owner = this.objects_by_id.get(object.owner);

      if (owner === undefined || owner === null) {
        return;
        /*  This prevents the full account from being looked up later
            owner = {id:object.owner, balances:{ } }
            owner.balances[object.asset_type] = object.id
            owner = Immutable.fromJS( owner )
            */
      }

      let balances = owner.get('balances');

      if (!balances) {
        owner = owner.set('balances', Immutable.Map());
      }

      owner = owner.setIn(['balances', object.asset_type], object.id);

      this.objects_by_id = this.objects_by_id.set(object.owner, owner);
    } else if (objectSpace === account_stats_prefix) {
      // ACCOUNT STATS OBJECT
      // console.log( "HISTORY CHANGED" )
      let prior_most_recent_op = prior ? prior.get('most_recent_op') : '2.9.0';

      if (prior_most_recent_op !== object.most_recent_op) {
        this.fetchRecentHistory(object.owner);
      }
    } else if (objectSpace === witness_prefix) {
      // WITNESS OBJECT
      if (this._isSubbedTo('witnesses', object.id)) {
        this.witness_by_account_id.set(object.witness_account, object.id);
        this.objects_by_vote_id.set(object.vote_id, object.id);
      } else {
        return;
      }
    } else if (objectSpace === committee_prefix) {
      // COMMITTEE MEMBER OBJECT
      if (this._isSubbedTo('committee', object.id)) {
        this.committee_by_account_id.set(object.committee_member_account, object.id);
        this.objects_by_vote_id.set(object.vote_id, object.id);
      } else {
        return;
      }
    } else if (objectSpace === account_prefix) {
      // ACCOUNT OBJECT
      current = current.set('active', Immutable.fromJS(object.active));
      current = current.set('owner', Immutable.fromJS(object.owner));
      current = current.set('options', Immutable.fromJS(object.options));
      current = current.set(
        'pending_dividend_payments',
        Immutable.fromJS(object.pending_dividend_payments)
      );
      current = current.set(
        'whitelisting_accounts',
        Immutable.fromJS(object.whitelisting_accounts)
      );
      current = current.set(
        'blacklisting_accounts',
        Immutable.fromJS(object.blacklisting_accounts)
      );
      current = current.set('whitelisted_accounts', Immutable.fromJS(object.whitelisted_accounts));
      current = current.set('blacklisted_accounts', Immutable.fromJS(object.blacklisted_accounts));
      this.objects_by_id = this.objects_by_id.set(object.id, current);
      this.accounts_by_name = this.accounts_by_name.set(object.name, object.id);
    } else if (objectSpace === asset_prefix) {
      // ASSET OBJECT
      this.assets_by_symbol = this.assets_by_symbol.set(object.symbol, object.id);
      let dynamic = current.get('dynamic');

      if (!dynamic) {
        let dad = this.getObject(object.dynamic_asset_data_id, true);

        if (!dad) {
          dad = Immutable.Map();
        }

        if (!dad.get('asset_id')) {
          dad = dad.set('asset_id', object.id);
        }

        this.objects_by_id = this.objects_by_id.set(object.dynamic_asset_data_id, dad);

        current = current.set('dynamic', dad);
        this.objects_by_id = this.objects_by_id.set(object.id, current);
      }

      let bitasset = current.get('bitasset');

      if (!bitasset && object.bitasset_data_id) {
        let bad = this.getObject(object.bitasset_data_id, true);

        if (!bad) {
          bad = Immutable.Map();
        }

        if (!bad.get('asset_id')) {
          bad = bad.set('asset_id', object.id);
        }

        this.objects_by_id = this.objects_by_id.set(object.bitasset_data_id, bad);

        current = current.set('bitasset', bad);
        this.objects_by_id = this.objects_by_id.set(object.id, current);
      }
    } else if (objectSpace === asset_dynamic_data_prefix) {
      // ASSET DYNAMIC DATA OBJECT
      let asset_id = current.get('asset_id');

      if (asset_id) {
        let asset_obj = this.getObject(asset_id);

        if (asset_obj && asset_obj.set) {
          asset_obj = asset_obj.set('dynamic', current);
          this.objects_by_id = this.objects_by_id.set(asset_id, asset_obj);
        }
      }
    } else if (objectSpace === worker_prefix) {
      // WORKER OBJECT
      this.objects_by_vote_id.set(object.vote_for, object.id);
      this.objects_by_vote_id.set(object.vote_against, object.id);
    } else if (objectSpace === bitasset_data_prefix) {
      // BITASSET DATA OBJECT
      let asset_id = current.get('asset_id');

      if (asset_id) {
        let asset = this.getObject(asset_id);

        if (asset) {
          asset = asset.set('bitasset', current);
          emitter.emit('bitasset-update', asset);
          this.objects_by_id = this.objects_by_id.set(asset_id, asset);
        }
      }
    } else if (objectSpace === call_order_prefix) {
      // CALL ORDER OBJECT
      // Update nested call_orders inside account object
      if (emit) {
        emitter.emit('call-order-update', object);
      }

      let account = this.objects_by_id.get(object.borrower);

      if (account && account.has('call_orders')) {
        let call_orders = account.get('call_orders');

        if (!call_orders.has(object.id)) {
          account = account.set('call_orders', call_orders.add(object.id));
          this.objects_by_id = this.objects_by_id.set(account.get('id'), account);
          // Force subscription to the object in the witness node by calling get_objects
          Apis.instance()
            .db_api()
            .exec('get_objects', [[object.id]]);
        }
      }
    } else if (objectSpace === order_prefix) {
      // LIMIT ORDER OBJECT
      let account = this.objects_by_id.get(object.seller);

      if (account && account.has('orders')) {
        let limit_orders = account.get('orders');

        if (!limit_orders.has(object.id)) {
          account = account.set('orders', limit_orders.add(object.id));
          this.objects_by_id = this.objects_by_id.set(account.get('id'), account);
          // Force subscription to the object in the witness node by calling get_objects
          Apis.instance()
            .db_api()
            .exec('get_objects', [[object.id]]);
        }
      }
    } else if (objectSpace === proposal_prefix) {
      // PROPOSAL OBJECT
      this.addProposalData(object.required_active_approvals, object.id);
      this.addProposalData(object.required_owner_approvals, object.id);
    } else if (objectSpace === tournament_prefix) {
      // TOURNAMENT OBJECT
      let priorState = prior.get('state');
      let newState = current.get('state');

      if (priorState !== newState) {
        this.tournament_ids_by_state = this.tournament_ids_by_state
          .map((stateMap, accountId) => stateMap.map((tournamentIdSet, stateString) => {
            if (stateString === priorState) {
              return tournamentIdSet.remove(object.id);
            }

            if (
              stateString === newState
                && (accountId === null
                  || current.getIn(['options', 'whitelist']).isEmpty()
                  || current.getIn(['options', 'whitelist']).includes(accountId))
            ) {
              return tournamentIdSet.add(object.id);
            }

            return tournamentIdSet;
          }));
      }

      if (this.last_tournament_id !== undefined) {
        this.setLastTournamentId(current.get('id'));
      }
    } else if (objectSpace === tournament_details_prefix) {
      let priorRegisteredPlayers = prior.get('registered_players');
      let newRegisteredPlayers = current.get('registered_players');

      if (priorRegisteredPlayers !== newRegisteredPlayers) {
        this.registered_tournament_ids_by_player = this.registered_tournament_ids_by_player.map(
          (tournamentIdsSet, accountId) => {
            if (newRegisteredPlayers.includes(accountId)) {
              return tournamentIdsSet.add(current.get('tournament_id'));
            }

            return tournamentIdsSet;

            // currently, you can't un-register for a tournament, so we don't have
            // to deal with removing from a list
          }
        );
      }
    }

    if (notify_subscribers) {
      this.notifySubscribers();
    }

    return current;
  }

  setLastTournamentId(current_tournament_id) {
    if (current_tournament_id === null) {
      if (!this.last_tournament_id) {
        this.last_tournament_id = current_tournament_id;
      }
    } else {
      let current_short_string = current_tournament_id.substring(tournament_prefix.length);
      let current_short = parseFloat(current_short_string);

      let last_short = -1;

      if (this.last_tournament_id) {
        last_short = parseFloat(this.last_tournament_id.substring(tournament_prefix.length));
      }

      if (current_short > last_short) {
        this.last_tournament_id = current_tournament_id;
      }
    }
  }

  getTournaments(last_tournament_id, limit = 5, start_tournament_id) {
    return Apis.instance()
      .db_api()
      .exec('get_tournaments', [last_tournament_id, limit, start_tournament_id])
      .then((tournaments) => {
        let list = Immutable.List();

        this.setLastTournamentId(null);

        if (tournaments && tournaments.length) {
          list = list.withMutations((l) => {
            tournaments.forEach((tournament) => {
              if (!this.objects_by_id.has(tournament.id)) {
                this._updateObject(tournament);
              }

              l.unshift(this.objects_by_id.get(tournament.id));
            });
          });
        }

        return list;
      });
  }

  getLastTournamentId() {
    return new Promise((resolve) => {
      if (this.last_tournament_id === undefined) {
        Apis.instance()
          .db_api()
          .exec('get_tournaments', [`${tournament_prefix}0`, 1, `${tournament_prefix}0`])
          .then((tournaments) => {
            this.setLastTournamentId(null);

            if (tournaments && tournaments.length) {
              tournaments.forEach((tournament) => {
                this._updateObject(tournament);
              });
            }

            resolve(this.last_tournament_id);
          });
      } else {
        resolve(this.last_tournament_id);
      }
    });
  }

  getObjectsByVoteIds(vote_ids) {
    let result = [];
    let missing = [];

    for (let i = 0; i < vote_ids.length; ++i) {
      let obj = this.objects_by_vote_id.get(vote_ids[i]);

      if (obj) {
        result.push(this.getObject(obj));
      } else {
        missing.push(vote_ids[i]);
      }
    }

    if (missing.length) {
      // we may need to fetch some objects
      Apis.instance()
        .db_api()
        .exec('lookup_vote_ids', [missing])
        .then(
          (vote_obj_array) => {
            for (let i = 0; i < vote_obj_array.length; ++i) {
              if (vote_obj_array[i]) {
                this._updateObject(vote_obj_array[i]);
                let immutableMapConvert = Immutable.fromJS(vote_obj_array[i]);
                result.push(immutableMapConvert);
              }
            }
          },
          (error) => console.log('Error looking up vote ids: ', error)
        );
    }

    return result;
  }

  getObjectByVoteID(vote_id) {
    let obj_id = this.objects_by_vote_id.get(vote_id);

    if (obj_id) {
      return this.getObject(obj_id);
    }

    return undefined;
  }

  getHeadBlockDate() {
    return ChainStore.timeStringToDate(this.head_block_time_string);
  }

  getEstimatedChainTimeOffset() {
    if (this.chain_time_offset.length === 0) {
      return 0;
    }

    // Immutable is fast, sorts numbers correctly, and leaves the original unmodified
    // This will fix itself if the user changes their clock
    let median_offset = Immutable.List(this.chain_time_offset)
      .sort()
      .get(Math.floor((this.chain_time_offset.length - 1) / 2));
    // console.log("median_offset", median_offset)
    return median_offset;
  }

  addProposalData(approvals, objectId) {
    approvals.forEach((id) => {
      let impactedAccount = this.objects_by_id.get(id);

      if (impactedAccount) {
        let proposals = impactedAccount.get('proposals');

        if (!proposals.includes(objectId)) {
          proposals = proposals.add(objectId);
          impactedAccount = impactedAccount.set('proposals', proposals);
          this._updateObject(impactedAccount.toJS());
        }
      }
    });
  }

  static timeStringToDate(time_string) {
    if (!time_string) {
      return new Date('1970-01-01T00:00:00.000Z');
    }

    // does not end in Z
    if (!/Z$/.test(time_string)) {
      // https://github.com/cryptonomex/graphene/issues/368
      time_string += 'Z';
    }

    return new Date(time_string);
  }

  __getBlocksForScan(lastBlock) {
    let db_api = Apis.instance().db_api();
    return new Promise((success) => {
      let scanToBlock = this.last_processed_block;

      if (lastBlock) {
        return success({lastBlock, scanToBlock});
      }

      db_api.exec('get_dynamic_global_properties', []).then((globalProperties) => {
        this.last_processed_block = globalProperties.head_block_number;
        scanToBlock = globalProperties.head_block_number - 2000;
        scanToBlock = scanToBlock < 0 ? 1 : scanToBlock;
        return success({
          lastBlock: this.last_processed_block,
          scanToBlock
        });
      });
    });
  }

  __bindBlock(lastBlock, scanToBlock, isInit) {
    let db_api = Apis.instance().db_api();
    return new Promise((success) => {
      db_api.exec('get_block', [lastBlock]).then((block) => {
        block.id = lastBlock;

        if (typeof block.timestamp === 'string') {
          block.timestamp += '+00:00';
        }

        block.timestamp = new Date(block.timestamp);
        this.getWitnessAccount(block.witness).then((witness) => {
          block.witness_account_name = witness.name;

          if (!this.recent_blocks_by_id.get(lastBlock)) {
            this.recent_blocks_by_id = this.recent_blocks_by_id.set(lastBlock, block);

            if (this.last_processed_block < lastBlock) {
              this.last_processed_block = lastBlock;
            }

            if (!isInit) {
              this.recent_blocks = this.recent_blocks.unshift(block);

              if (this.recent_blocks.size > block_stack_size) {
                this.recent_blocks = this.recent_blocks.pop();
              }
            } else if (this.recent_blocks.size < block_stack_size) {
              this.recent_blocks = this.recent_blocks.push(block);
            }

            block.transactions.forEach((tx) => tx.operations.forEach((op) => {
              op[1].block_id = lastBlock;
              op[1].created_at = block.timestamp;

              if (!isInit) {
                this.recent_operations = this.recent_operations.unshift(op);
              } else {
                if (this.recent_operations.size < operations_stack_size) {
                  this.recent_operations = this.recent_operations.push(op);
                }

                if (
                  this.recent_operations.size >= operations_stack_size
                    && this.recent_blocks.size >= block_stack_size
                ) {
                  scanToBlock = lastBlock;
                }
              }

              if (this.recent_operations.size > operations_stack_size) {
                this.recent_operations = this.recent_operations.pop();
              }
            }));
          }

          lastBlock--;

          if (lastBlock <= scanToBlock) {
            return success();
          }

          this.__bindBlock(lastBlock, scanToBlock, isInit).then(() => success());
        });
      });
    });
  }

  fetchRecentOperations(lastBlock = null) {
    if (lastBlock && !this.last_processed_block) {
      return;
    }

    let isInit = !lastBlock;

    this.__getBlocksForScan(lastBlock).then(({lastBlock: last, scanToBlock}) => {
      this.__bindBlock(last, scanToBlock, isInit).then(() => {
        if (isInit) {
          this.store_initialized = true;
        }
      });
    });
  }

  getRecentBlocks() {
    return this.recent_blocks;
  }

  getRecentOperations() {
    if (!this.store_initialized) {
      return Immutable.List();
    }

    return this.recent_operations;
  }
}

const chain_store = new ChainStore();

function FetchChainObjects(method, object_ids, timeout) {
  let get_object = method.bind(chain_store);

  return new Promise((resolve, reject) => {
    let timeout_handle = null;

    function onUpdate(not_subscribed_yet = false) {
      let res = object_ids.map((id) => get_object(id));

      if (res.findIndex((o) => o === undefined) === -1) {
        if (timeout_handle) {
          clearTimeout(timeout_handle);
        }

        if (!not_subscribed_yet) {
          chain_store.unsubscribe(onUpdate);
        }

        resolve(res);
        return true;
      }

      return false;
    }

    let resolved = onUpdate(true);

    if (!resolved) {
      chain_store.subscribe(onUpdate);
    }

    if (timeout && !resolved) {
      timeout_handle = setTimeout(() => {
        chain_store.unsubscribe(onUpdate);
        reject(new Error('timeout'));
      }, timeout);
    }
  });
}

chain_store.FetchChainObjects = FetchChainObjects;

function FetchChain(methodName, objectIds, timeout = 1900) {
  let method = chain_store[methodName];

  if (!method) {
    throw new Error(`ChainStore does not have method ${methodName}`);
  }

  let arrayIn = Array.isArray(objectIds);

  if (!arrayIn) {
    objectIds = [objectIds];
  }

  return chain_store
    .FetchChainObjects(method, Immutable.List(objectIds), timeout)
    .then((res) => (arrayIn ? res : res.get(0)));
}

chain_store.FetchChain = FetchChain;

export default chain_store;

/**
    Account names may contain one or more names separated by a dot.
    Each name needs to start with a letter and may contain
    numbers, or well placed dashes.
    @see is_valid_name graphene/libraries/chain/protocol/account.cpp
*/
let id_regex = /\b\d+\.\d+\.(\d+)\b/;

const ChainValidation = {
  is_account_name(value, allow_too_short = false) {
    let label;
    let ref;

    if (ChainValidation.is_empty(value)) {
      return false;
    }

    let {length} = value;

    if ((!allow_too_short && length < 3) || length > 63) {
      return false;
    }

    ref = value.split('.');

    for (let i = 0, len = ref.length; i < len; i++) {
      label = ref[i];

      if (!(/^[a-z][a-z0-9-]*$/.test(label) && !/--/.test(label) && /[a-z0-9]$/.test(label))) {
        return false;
      }
    }

    return true;
  },

  is_object_id(obj_id) {
    if (typeof obj_id !== 'string') {
      return false;
    }

    let match = id_regex.exec(obj_id);
    return match !== null && obj_id.split('.').length === 3;
  },

  is_empty(value) {
    return value == null || value.length === 0;
  },

  is_account_name_error(value, allow_too_short) {
    let label;
    let ref;
    let suffix;

    if (allow_too_short == null) {
      allow_too_short = false;
    }

    suffix = 'Account name should ';

    if (ChainValidation.is_empty(value)) {
      return `${suffix}not be empty.`;
    }

    let {length} = value;

    if (!allow_too_short && length < 3) {
      return `${suffix}be longer.`;
    }

    if (length > 63) {
      return `${suffix}be shorter.`;
    }

    if (/\./.test(value)) {
      suffix = 'Each account segment should ';
    }

    ref = value.split('.');

    for (let i = 0, len = ref.length; i < len; i++) {
      label = ref[i];

      if (!/^[~a-z]/.test(label)) {
        return `${suffix}start with a letter.`;
      }

      if (!/^[~a-z0-9-]*$/.test(label)) {
        return `${suffix}have only letters, digits, or dashes.`;
      }

      if (/--/.test(label)) {
        return `${suffix}have only one dash in a row.`;
      }

      if (!/[a-z0-9]$/.test(label)) {
        return `${suffix}end with a letter or digit.`;
      }

      if (!(label.length >= 3)) {
        return `${suffix}be longer`;
      }
    }

    return null;
  },

  is_cheap_name(account_name) {
    return /[0-9-]/.test(account_name) || !/[aeiouy]/.test(account_name);
  },

  is_empty_user_input(value) {
    if (ChainValidation.is_empty(value)) {
      return true;
    }

    if (`${value}`.trim() === '') {
      return true;
    }

    return false;
  },

  required(value, field_name = '') {
    if (ChainValidation.is_empty(value)) {
      throw new Error(`value required for ${field_name}: ${value}`);
    }

    return value;
  },

  /** @see is_valid_symbol graphene/libraries/chain/protocol/asset_ops.cpp */
  is_valid_symbol_error(value) {
    let suffix = 'Asset name should ';

    if (ChainValidation.is_empty(value)) {
      return `${suffix}not be empty.`;
    }

    if (value.split('.').length > 2) {
      return `${suffix}have only one dot.`;
    }

    if (value.length < 3) {
      return `${suffix}be longer.`;
    }

    if (value.length > 16) {
      return `${suffix}be shorter.`;
    }

    if (!/^[A-Z]/.test(value)) {
      return `${suffix}start with a letter`;
    }

    if (!/[A-Z]$/.test(value)) {
      return `${suffix}end with a letter`;
    }

    if (/^[A-Z0-9\.]$/.test(value)) {
      return `${suffix}contain only letters numbers and perhaps a dot.`;
    }

    return null;
  }
};

export default ChainValidation;

import ee from 'event-emitter';

let _emitter;

export default {
  emitter() {
    if (!_emitter) {
      _emitter = ee({});
    }

    return _emitter;
  }
};

import {hash} from '../../ecc';
import {Serializer, types} from '../../serializer';

let ByteBuffer = require('bytebuffer');
let secureRandom = require('secure-random');

let {
  uint64,
  enumeration,
  sha256
} = types;

let rock_paper_scissors_gesture = enumeration(['rock', 'paper', 'scissors', 'spock', 'lizard']);

let rock_paper_scissors_throw = new Serializer('rock_paper_scissors_throw', {
  nonce1: uint64,
  nonce2: uint64,
  gesture: rock_paper_scissors_gesture
});

let rock_paper_scissors_throw_commit = new Serializer('rock_paper_scissors_throw_commit', {
  nonce1: uint64,
  throw_hash: sha256
});

let rock_paper_scissors_throw_reveal = new Serializer('rock_paper_scissors_throw_reveal', {
  nonce2: uint64,
  gesture: rock_paper_scissors_gesture
});

class GameMoves {
  static createCommitAndRevealMoveOperations(move_type) {
    let nonce1 = ByteBuffer.wrap(secureRandom.randomArray(8)).readUint64();
    let nonce2 = ByteBuffer.wrap(secureRandom.randomArray(8)).readUint64();

    let throw_object = rock_paper_scissors_throw.fromObject({
      nonce1,
      nonce2,
      gesture: move_type
    });

    let throw_buffer = rock_paper_scissors_throw.toBuffer(throw_object);
    let throw_hash = hash.sha256(throw_buffer);

    let commit_move_operation_object = rock_paper_scissors_throw_commit.fromObject({
      nonce1,
      throw_hash
    });
    let reveal_move_operation_object = rock_paper_scissors_throw_reveal.fromObject({
      nonce2,
      gesture: move_type
    });

    let commit_move_operation = rock_paper_scissors_throw_commit.toObject(
      commit_move_operation_object
    );
    let reveal_move_operation = rock_paper_scissors_throw_reveal.toObject(
      reveal_move_operation_object
    );

    return [commit_move_operation, reveal_move_operation];
  }
}

export default GameMoves;

import assert from 'assert';

/**
    Convert 12.34 with a precision of 3 into 12340

    @arg {number|string} number - Use strings for large numbers.
    @arg {number} precision - number of implied decimal places (usually causes right zero padding)
    @return {string} -
*/

const NumberUtils = {
  toImpliedDecimal: function toImpliedDecimal(number, precision) {
    if (typeof number === 'number') {
      assert(number <= 9007199254740991, 'overflow');
      number = `${number}`;
    } else if (number.toString) {
      number = number.toString();
    }

    assert(
      typeof number === 'string',
      `number should be an actual number or string: ${typeof number}`
    );
    number = number.trim();
    assert(/^[0-9]*\.?[0-9]*$/.test(number), `Invalid decimal number ${number}`);

    let [whole = '', decimal = ''] = number.split('.');

    let padding = precision - decimal.length;
    assert(
      padding >= 0,
      `Too many decimal digits in ${number} to create an implied decimal of ${precision}`
    );

    for (let i = 0; i < padding; i++) {
      decimal += '0';
    }

    while (whole.charAt(0) === '0') {
      whole = whole.substring(1);
    }

    return whole + decimal;
  }
};

export default NumberUtils;

import {Long} from 'bytebuffer';

import v from '../../serializer/src/SerializerValidation';

let DB_MAX_INSTANCE_ID = Long.fromNumber(Math.pow(2, 48) - 1);

class ObjectId {
  constructor(space, type, instance) {
    this.space = space;
    this.type = type;
    this.instance = instance;
    let instance_string = this.instance.toString();
    let id = `${this.space}.${this.type}.${instance_string}`;

    if (!v.is_digits(instance_string)) {
      throw new `Invalid object id ${id}`();
    }
  }

  static fromString(value) {
    if (value.space !== undefined && value.type !== undefined && value.instance !== undefined) {
      return value;
    }

    let params = v.require_match(
      /^([0-9]+)\.([0-9]+)\.([0-9]+)$/,
      v.required(value, 'ObjectId'),
      'ObjectId'
    );
    return new ObjectId(
      parseInt(params[1], 10),
      parseInt(params[2], 10),
      Long.fromString(params[3])
    );
  }

  static fromLong(long) {
    let space = long.shiftRight(56).toInt();
    let type = long.shiftRight(48).toInt() & 0x00ff; // eslint-disable-line
    let instance = long.and(DB_MAX_INSTANCE_ID);
    return new ObjectId(space, type, instance);
  }

  static fromByteBuffer(b) {
    return ObjectId.fromLong(b.readUint64());
  }

  toLong() {
    return Long.fromNumber(this.space)
      .shiftLeft(56)
      .or(
        Long.fromNumber(this.type)
          .shiftLeft(48)
          .or(this.instance)
      );
  }

  appendByteBuffer(b) {
    return b.writeUint64(this.toLong());
  }

  toString() {
    return `${this.space}.${this.type}.${this.instance.toString()}`;
  }
}

export default ObjectId;

import assert from 'assert';
import {Apis, ChainConfig} from '../../ws';
import {Signature, PublicKey, hash} from '../../ecc';
import {ops} from '../../serializer';

import ChainTypes from './ChainTypes';

class TransactionBuilder {
  constructor() {
    this.ref_block_num = 0;
    this.ref_block_prefix = 0;
    this.expiration = 0;
    this.operations = [];
    this.signatures = [];
    this.signer_private_keys = [];

    this.head_block_time_string = null;
    this.committee_min_review = null;

    // semi-private method bindings
    this._broadcast = this._broadcast.bind(this);
  }

  /**
        @arg {string} name - like "transfer"
        @arg {object} operation - JSON matchching the operation's format
    */
  add_type_operation(name, operation) {
    this.add_operation(this.get_type_operation(name, operation));
  }

  /**
        This does it all: set fees, finalize, sign, and broadcast (if wanted).

        @arg {ConfidentialWallet} cwallet - must be unlocked, used to gather signing keys

        @arg {array<string>} [signer_pubkeys = null] - Optional ["GPHAbc9Def0...", ...].  These are
        additional signing keys.  Some balance claims require propritary address formats, the
        witness node can't tell us which ones are needed so they must be passed in.  If the
        witness node can figure out a signing key (mostly all other transactions), it should
        not be passed in here.

        @arg {boolean} [broadcast = false]
    */
  process_transaction(cwallet, signer_pubkeys = null, broadcast = false) {
    let {wallet_object} = cwallet.wallet;

    if (Apis.instance().chain_id !== wallet_object.get('chain_id')) {
      let wallet_chain_id = wallet_object.get('chain_id');
      let api_chain_id = Apis.instance().chain_id;

      let error = new Error(
        `Mismatched chain_id; expecting ${wallet_chain_id}, but got ${api_chain_id}`
      );

      return Promise.reject(error);
    }

    return this.set_required_fees().then(() => {
      let signer_pubkeys_added = {};

      if (signer_pubkeys) {
        // Balance claims are by address, only the private
        // key holder can know about these additional
        // potential keys.
        let pubkeys = cwallet.getPubkeys_having_PrivateKey(signer_pubkeys);

        if (!pubkeys.length) {
          throw new Error('Missing signing key');
        }

        let keys = Object.keys(pubkeys);

        for (let i = 0, len = keys.length; i < len; i++) {
          let pubkey_string = pubkeys[i];
          let private_key = cwallet.getPrivateKey(pubkey_string);
          this.add_signer(private_key, pubkey_string);
          signer_pubkeys_added[pubkey_string] = true;
        }
      }

      return this.get_potential_signatures()
        .then(({pubkeys, addys}) => {
          let my_pubkeys = cwallet.getPubkeys_having_PrivateKey(pubkeys, addys);

          return this.get_required_signatures(my_pubkeys).then((required_pubkeys) => {
            let keys = Object.keys(required_pubkeys);

            for (let i = 0, len = keys.length; i < len; i++) {
              let pubkey_string = keys[i];

              if (!signer_pubkeys_added[pubkey_string]) {
                let private_key = cwallet.getPrivateKey(pubkey_string);

                // This should not happen, get_required_signatures will only
                // returned keys from my_pubkeys
                if (!private_key) {
                  throw new Error(`Missing signing key for ${pubkey_string}`);
                }

                this.add_signer(private_key, pubkey_string);
              }
            }
          });
        })
        .then(() => (broadcast ? this.broadcast() : this.serialize()));
    });
  }

  /** Typically this is called automatically just prior to signing.
   * Once finalized this transaction can not be changed. */
  finalize() {
    return new Promise((resolve) => {
      if (this.tr_buffer) {
        throw new Error('already finalized');
      }

      resolve(
        Apis.instance()
          .db_api()
          .exec('get_objects', [['2.1.0']])
          .then((r) => {
            this.head_block_time_string = r[0].time;

            if (this.expiration === 0) {
              this.expiration = this.base_expiration_sec() + ChainConfig.expire_in_secs;
            }

            this.ref_block_num = r[0].head_block_number & 0xffff; // eslint-disable-line
            this.ref_block_prefix = Buffer.from(r[0].head_block_id, 'hex').readUInt32LE(4);
            // DEBUG console.log("ref_block",@ref_block_num,@ref_block_prefix,r)

            let iterable = this.operations;

            for (let i = 0, len = iterable.length; i < len; i++) {
              let op = iterable[i];

              if (op[1].finalize) {
                op[1].finalize();
              }

              let _type = ops.operation.st_operations[op[0]];
              let hexBuffer = _type.toBuffer(op[1]).toString('hex');
              console.log(
                'Operation %s: %O => %s (%d bytes)',
                _type.operation_name,
                op[1],
                hexBuffer,
                hexBuffer.length / 2
              );
            }

            this.tr_buffer = ops.transaction.toBuffer(this);
          })
      );
    });
  }

  /** @return {string} hex transaction ID */
  id() {
    if (!this.tr_buffer) {
      throw new Error('not finalized');
    }

    return hash
      .sha256(this.tr_buffer)
      .toString('hex')
      .substring(0, 40);
  }

  /**
        Typically one will use {@link this.add_type_operation} instead.
        @arg {array} operation - [operation_id, operation]
    */
  add_operation(operation) {
    if (this.tr_buffer) {
      throw new Error('already finalized');
    }

    assert(operation, 'operation');

    if (!Array.isArray(operation)) {
      throw new Error('Expecting array [operation_id, operation]');
    }

    this.operations.push(operation);
  }

  get_type_operation(name, operation) {
    if (this.tr_buffer) {
      throw new Error('already finalized');
    }

    assert(name, 'name');
    assert(operation, 'operation');
    let _type = ops[name];
    assert(_type, `Unknown operation ${name}`);
    let operation_id = ChainTypes.operations[_type.operation_name];

    if (operation_id === undefined) {
      throw new Error(`unknown operation: ${_type.operation_name}`);
    }

    if (!operation.fee) {
      operation.fee = {amount: 0, asset_id: 0};
    }

    if (name === 'proposal_create') {
      /*
            * Proposals involving the committee account require a review
            * period to be set, look for them here
            */
      let requiresReview = false;
      let extraReview = 0;

      operation.proposed_ops.forEach((op) => {
        const COMMITTE_ACCOUNT = 0;
        let key;

        switch (op.op[0]) {
          case 0: // transfer
            key = 'from';
            break;

          case 6: // account_update
          case 17: // asset_settle
            key = 'account';
            break;

          case 10: // asset_create
          case 11: // asset_update
          case 12: // asset_update_bitasset
          case 13: // asset_update_feed_producers
          case 14: // asset_issue
          case 18: // asset_global_settle
          case 43: // asset_claim_fees
            key = 'issuer';
            break;

          case 15: // asset_reserve
            key = 'payer';
            break;

          case 16: // asset_fund_fee_pool
            key = 'from_account';
            break;

          case 22: // proposal_create
          case 23: // proposal_update
          case 24: // proposal_delete
            key = 'fee_paying_account';
            break;

          case 31: // committee_member_update_global_parameters
            requiresReview = true;
            extraReview = 60 * 60 * 24 * 13; // Make the review period 2 weeks total
            break;
          default:
            break;
        }

        if (key in op.op[1] && op.op[1][key] === COMMITTE_ACCOUNT) {
          requiresReview = true;
        }
      });

      if (!operation.expiration_time) {
        let experationTime = this.base_expiration_sec();
        operation.expiration_time = experationTime + ChainConfig.expire_in_secs_proposal;
      }

      if (requiresReview) {
        let one_day = 24 * 60 * 60; // One day in seconds
        let max = Math.max(
          this.commitee_min_review,
          one_day,
          ChainConfig.review_in_secs_committee
        );

        operation.review_period_seconds = extraReview + max;
        /*
                * Expiration time must be at least equal to
                * now + review_period_seconds, so we add one hour to make sure
                */
        operation.expiration_time += 60 * 60 + extraReview;
      }
    }

    let operation_instance = _type.fromObject(operation);
    return [operation_id, operation_instance];
  }

  /* optional: fetch the current head block */

  update_head_block() {
    return Apis.instance()
      .db_api()
      .exec('get_objects', [['2.0.0', '2.1.0']])
      .then((res) => {
        let [g, r] = res;
        this.head_block_time_string = r[0].time;
        this.committee_min_review = g[0].parameters.committee_proposal_review_period;
      });
  }

  /** optional: there is a deafult expiration */
  set_expire_seconds(sec) {
    if (this.tr_buffer) {
      throw new Error('already finalized');
    }

    this.expiration = this.base_expiration_sec() + sec;

    return this.expiration;
  }

  /* Wraps this transaction in a proposal_create transaction */
  propose(proposal_create_options) {
    if (this.tr_buffer) {
      throw new Error('already finalized');
    }

    if (!this.operations.length) {
      throw new Error('add operation first');
    }

    assert(proposal_create_options, 'proposal_create_options');
    assert(
      proposal_create_options.fee_paying_account,
      'proposal_create_options.fee_paying_account'
    );

    let proposed_ops = this.operations.map((op) => ({op}));

    this.operations = [];
    this.signatures = [];
    this.signer_private_keys = [];
    proposal_create_options.proposed_ops = proposed_ops;
    this.add_type_operation('proposal_create', proposal_create_options);
    return this;
  }

  has_proposed_operation() {
    let hasProposed = false;

    for (let i = 0; i < this.operations.length; i++) {
      if ('proposed_ops' in this.operations[i][1]) {
        hasProposed = true;
        break;
      }
    }

    return hasProposed;
  }

  /** optional: the fees can be obtained from the witness node */
  set_required_fees(asset_id) {
    let fee_pool;

    if (this.tr_buffer) {
      throw new Error('already finalized');
    }

    if (!this.operations.length) {
      throw new Error('add operations first');
    }

    let operations = [];

    for (let i = 0, len = this.operations.length; i < len; i++) {
      let op = this.operations[i];
      operations.push(ops.operation.toObject(op));
    }

    if (!asset_id) {
      let op1_fee = operations[0][1].fee;

      if (op1_fee && op1_fee.asset_id !== null) {
        asset_id = op1_fee.asset_id;
      } else {
        asset_id = '1.3.0';
      }
    }

    let promises = [
      Apis.instance()
        .db_api()
        .exec('get_required_fees', [operations, asset_id])
    ];

    if (asset_id !== '1.3.0') {
      // This handles the fallback to paying fees in BTS if the fee pool is empty.
      promises.push(
        Apis.instance()
          .db_api()
          .exec('get_required_fees', [operations, '1.3.0'])
      );
      promises.push(
        Apis.instance()
          .db_api()
          .exec('get_objects', [[asset_id]])
      );
    }

    return Promise.all(promises).then((results) => {
      let [fees, coreFees, asset] = results;
      asset = asset ? asset[0] : null;

      let dynamicPromise = asset_id !== '1.3.0' && asset
        ? Apis.instance()
          .db_api()
          .exec('get_objects', [[asset.dynamic_asset_data_id]])
        : new Promise((resolve) => {
          resolve();
        });

      return dynamicPromise.then((dynamicObject) => {
        if (asset_id !== '1.3.0') {
          fee_pool = dynamicObject ? dynamicObject[0].fee_pool : 0;
          let totalFees = 0;

          for (let j = 0, fee; j < coreFees.length; j++) {
            fee = coreFees[j];
            totalFees += fee.amount;
          }

          if (totalFees > parseInt(fee_pool, 10)) {
            fees = coreFees;
            asset_id = '1.3.0';
          }
        }

        // Proposed transactions need to be flattened
        let flat_assets = [];

        let flatten = (obj) => {
          if (Array.isArray(obj)) {
            for (let k = 0, len = obj.length; k < len; k++) {
              let item = obj[k];
              flatten(item);
            }
          } else {
            flat_assets.push(obj);
          }
        };

        flatten(fees);

        let asset_index = 0;

        let set_fee = (operation) => {
          if (
            !operation.fee
            || operation.fee.amount === 0
            || (operation.fee.amount.toString && operation.fee.amount.toString() === '0') // Long
          ) {
            operation.fee = flat_assets[asset_index];
            // console.log("new operation.fee", operation.fee)
          } else {
            // console.log("old operation.fee", operation.fee)
          }

          asset_index++;

          if (operation.proposed_ops) {
            let result = [];

            for (let y = 0; y < operation.proposed_ops.length; y++) {
              result.push(set_fee(operation.proposed_ops[y].op[1]));
            }

            return result;
          }
        };

        for (let i = 0; i < this.operations.length; i++) {
          set_fee(this.operations[i][1]);
        }
      });
      // DEBUG console.log('... get_required_fees',operations,asset_id,flat_assets)
    });
  }

  get_potential_signatures() {
    let tr_object = ops.signed_transaction.toObject(this);
    return Promise.all([
      Apis.instance()
        .db_api()
        .exec('get_potential_signatures', [tr_object]),
      Apis.instance()
        .db_api()
        .exec('get_potential_address_signatures', [tr_object])
    ]).then((results) => ({pubkeys: results[0], addys: results[1]}));
  }

  get_required_signatures(available_keys) {
    if (!available_keys.length) {
      return Promise.resolve([]);
    }

    let tr_object = ops.signed_transaction.toObject(this);
    // DEBUG console.log('... tr_object',tr_object)
    return Apis.instance()
      .db_api()
      .exec('get_required_signatures', [tr_object, available_keys])
      .then((required_public_keys) => required_public_keys);
  }

  add_signer(private_key, public_key = private_key.toPublicKey()) {
    assert(private_key.d, 'required PrivateKey object');

    if (this.signed) {
      throw new Error('already signed');
    }

    if (!public_key.Q) {
      public_key = PublicKey.fromPublicKeyString(public_key);
    }

    // prevent duplicates
    let spHex = private_key.toHex();

    let keys = Object.keys(this.signer_private_keys);

    for (let i = 0, len = keys.length; i < len; i++) {
      let sp = this.signer_private_keys[keys[i]];

      if (sp[0].toHex() === spHex) {
        return;
      }
    }

    this.signer_private_keys.push([private_key, public_key]);
  }

  sign(chain_id = Apis.instance().chain_id) {
    if (!this.tr_buffer) {
      throw new Error('not finalized');
    }

    if (this.signed) {
      throw new Error('already signed');
    }

    if (!this.signer_private_keys.length) {
      throw new Error('Transaction was not signed. Do you have a private key? [no_signers]');
    }

    let end = this.signer_private_keys.length;

    for (let i = 0; end > 0 ? i < end : i > end; end > 0 ? i++ : i++) {
      let [private_key, public_key] = this.signer_private_keys[i];
      let sig = Signature.signBuffer(
        Buffer.concat([Buffer.from(chain_id, 'hex'), this.tr_buffer]),
        private_key,
        public_key
      );
      this.signatures.push(sig.toBuffer());
    }

    this.signer_private_keys = [];
    this.signed = true;
  }

  serialize() {
    return ops.signed_transaction.toObject(this);
  }

  toObject() {
    return ops.signed_transaction.toObject(this);
  }

  broadcast(was_broadcast_callback) {
    if (this.tr_buffer) {
      return this._broadcast(was_broadcast_callback);
    }

    return this.finalize().then(() => this._broadcast(was_broadcast_callback));
  }

  _broadcast(was_broadcast_callback) {
    return new Promise((resolve, reject) => {
      if (!this.signed) {
        this.sign();
      }

      if (!this.tr_buffer) {
        throw new Error('not finalized');
      }

      if (!this.signatures.length) {
        throw new Error('not signed');
      }

      if (!this.operations.length) {
        throw new Error('no operations');
      }

      let tr_object = ops.signed_transaction.toObject(this);
      // console.log('... broadcast_transaction_with_callback !!!')
      Apis.instance()
        .network_api()
        .exec('broadcast_transaction_with_callback', [(res) => resolve(res), tr_object])
        .then(() => {
          // console.log('... broadcast success, waiting for callback')
          if (was_broadcast_callback) {
            was_broadcast_callback();
          }
        })
        .catch((error) => {
          // console.log may be redundant for network errors, other errors could occur
          console.log(error);
          let {message} = error;

          if (!message) {
            message = '';
          }

          reject(
            new Error(
              `${message}\n`
                + 'peerplays-crypto '
                + ` digest ${hash
                  .sha256(this.tr_buffer)
                  .toString('hex')} transaction ${this.tr_buffer.toString('hex')} ${JSON.stringify(
                  tr_object
                )}`
            )
          );
        });
    });
  }

  static timeStringToDate(time_string) {
    if (!time_string) {
      time_string = '1970-01-01T00:00:00.000Z';
    }

    if (!/Z$/.test(time_string)) {
      // does not end in Z
      // https://github.com/cryptonomex/graphene/issues/368
      time_string += 'Z';
    }

    return new Date(time_string);
  }

  getHeadBlockDate() {
    return TransactionBuilder.timeStringToDate(this.head_block_time_string);
  }

  base_expiration_sec() {
    let head_block_sec = Math.ceil(this.getHeadBlockDate().getTime() / 1000);
    let now_sec = Math.ceil(Date.now() / 1000);

    // The head block time should be updated every 3 seconds.  If it isn't
    // then help the transaction to expire (use head_block_sec)
    if (now_sec - head_block_sec > 30) {
      return head_block_sec;
    }

    // If the user's clock is very far behind, use the head block time.
    return Math.max(now_sec, head_block_sec);
  }
}

