class FastParser {
  static fixed_data(b, len, buffer) {
    if (!b) {
      return;
    }
    if (buffer) {
      let data = buffer.slice(0, len).toString('binary');
      b.append(data, 'binary');
      while (len-- > data.length) {
        b.writeUint8(0);
      }
    } else {
      let b_copy = b.copy(b.offset, b.offset + len);
      b.skip(len);
      return Buffer.from(b_copy.toBinary(), 'binary');
    }
  }

  static public_key(b, public_key) {
    let buffer;
    if (!b) {
      return;
    }
    if (public_key) {
      buffer = public_key.toBuffer();
      b.append(buffer.toString('binary'), 'binary');
    } else {
      buffer = FastParser.fixed_data(b, 33);
      return PublicKey.fromBuffer(buffer);
    }
  }

  static ripemd160(b, ripemd160) {
    if (!b) {
      return;
    }
    if (ripemd160) {
      FastParser.fixed_data(b, 20, ripemd160);
    } else {
      return FastParser.fixed_data(b, 20);
    }
  }

  static sha256(b, sha256) {
    if (!b) {
      return;
    }
    if (sha256) {
      FastParser.fixed_data(b, 32, sha256);
    } else {
      return FastParser.fixed_data(b, 32);
    }
  }

  static time_point_sec(b, epoch) {
    if (epoch) {
      epoch = Math.ceil(epoch / 1000);
      b.writeInt32(epoch);
    } else {
      epoch = b.readInt32(); // fc::time_point_sec
      return new Date(epoch * 1000);
    }
  }
}

export default FastParser;

import {Long} from 'bytebuffer';
import ChainTypes from '../../chain/src/ChainTypes';

let MAX_SAFE_INT = 9007199254740991;
let MIN_SAFE_INT = -9007199254740991;

/**
    Most validations are skipped and the value returned unchanged when an empty string,
    null, or undefined is encountered (except "required").
    Validations support a string format for dealing with large numbers.
*/
const _my = {
  is_empty(value) {
    return value === null || value === undefined;
  },

  required(value, field_name = '') {
    if (this.is_empty(value)) {
      throw new Error(`value required ${field_name} | ${value}`);
    }

    return value;
  },

  require_long(value, field_name = '') {
    if (!Long.isLong(value)) {
      throw new Error(`Long value required ${field_name} | ${value}`);
    }

    return value;
  },

  string(value) {
    if (this.is_empty(value)) {
      return value;
    }

    if (typeof value !== 'string') {
      throw new Error(`string required: ${value}`);
    }

    return value;
  },

  number(value) {
    if (this.is_empty(value)) {
      return value;
    }

    if (typeof value !== 'number') {
      throw new Error(`number required: ${value}`);
    }

    return value;
  },

  whole_number(value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    if (/\./.test(value)) {
      throw new Error(`whole number required ${field_name} ${value}`);
    }

    return value;
  },

  unsigned(value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    if (/-/.test(value)) {
      throw new Error(`unsigned required ${field_name} ${value}`);
    }

    return value;
  },

  is_digits(value) {
    if (typeof value === 'number') {
      return true;
    }

    return /^[0-9]+$/.test(value);
  },

  to_number(value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    this.no_overflow53(value, field_name);
    let int_value = (() => {
      if (typeof value === 'number') {
        return value;
      }

      return parseInt(value, 10);
    })();
    return int_value;
  },

  to_long(value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    if (Long.isLong(value)) {
      return value;
    }

    this.no_overflow64(value, field_name);

    if (typeof value === 'number') {
      value = `${value}`;
    }

    return Long.fromString(value);
  },

  to_ulong(value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    if (Long.isLong(value)) {
      return value;
    }

    this.no_overflow64(value, field_name, true);

    if (typeof value === 'number') {
      value = `${value}`;
    }

    return Long.fromString(value, true);
  },

  to_string(value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    if (typeof value === 'string') {
      return value;
    }

    if (typeof value === 'number') {
      this.no_overflow53(value, field_name);
      return `${value}`;
    }

    if (Long.isLong(value)) {
      return value.toString();
    }

    throw new Error(`unsupported type ${field_name}: (${typeof value}) ${value}`);
  },

  require_test(regex, value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    if (!regex.test(value)) {
      throw new Error(`unmatched ${regex} ${field_name} ${value}`);
    }

    return value;
  },

  require_match(regex, value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    let match = value.match(regex);

    if (match === null) {
      throw new Error(`unmatched ${regex} ${field_name} ${value}`);
    }

    return match;
  },

  require_object_id(value, field_name) {
    return this.require_match(/^([0-9]+)\.([0-9]+)\.([0-9]+)$/, value, field_name);
  },

  // Does not support over 53 bits
  require_range(min, max, value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    let num = this.to_number(value);

    if (num < min || num > max) {
      throw new Error(`out of range ${value} ${field_name} ${value}`);
    }

    return value;
  },

  require_object_type(reserved_spaces = 1, type, value, field_name = '') {
    if (this.is_empty(value)) {
      return value;
    }

    let object_type = ChainTypes.object_type[type];

    if (!object_type) {
      throw new Error(`Unknown object type ${type} ${field_name} ${value}`);
    }

    let re = new RegExp(`${reserved_spaces}\.${object_type}\.[0-9]+$`);

    if (!re.test(value)) {
      throw new Error(
        `Expecting ${type} in format `
          + `${reserved_spaces}.${object_type}.[0-9]+ `
          + `instead of ${value} ${field_name} ${value}`
      );
    }

    return value;
  },

  get_instance(reserve_spaces, type, value, field_name) {
    if (this.is_empty(value)) {
      return value;
    }

    this.require_object_type(reserve_spaces, type, value, field_name);
    return this.to_number(value.split('.')[2]);
  },

  require_relative_type(type, value, field_name) {
    this.require_object_type(0, type, value, field_name);
    return value;
  },

  get_relative_instance(type, value, field_name) {
    if (this.is_empty(value)) {
      return value;
    }

    this.require_object_type(0, type, value, field_name);
    return this.to_number(value.split('.')[2]);
  },

  require_protocol_type(type, value, field_name) {
    this.require_object_type(1, type, value, field_name);
    return value;
  },

  get_protocol_instance(type, value, field_name) {
    if (this.is_empty(value)) {
      return value;
    }

    this.require_object_type(1, type, value, field_name);
    return this.to_number(value.split('.')[2]);
  },

  get_protocol_type(value, field_name) {
    if (this.is_empty(value)) {
      return value;
    }

    this.require_object_id(value, field_name);
    let values = value.split('.');
    return this.to_number(values[1]);
  },

  get_protocol_type_name(value, field_name) {
    if (this.is_empty(value)) {
      return value;
    }

    let type_id = this.get_protocol_type(value, field_name);
    return Object.keys(ChainTypes.object_type)[type_id];
  },

  require_implementation_type(type, value, field_name) {
    this.require_object_type(2, type, value, field_name);
    return value;
  },

  get_implementation_instance(type, value, field_name) {
    if (this.is_empty(value)) {
      return value;
    }

    this.require_object_type(2, type, value, field_name);
    return this.to_number(value.split('.')[2]);
  },

  // signed / unsigned decimal
  no_overflow53(value, field_name = '') {
    if (typeof value === 'number') {
      if (value > MAX_SAFE_INT || value < MIN_SAFE_INT) {
        throw new Error(`overflow ${field_name} ${value}`);
      }

      return;
    }

    if (typeof value === 'string') {
      let int = parseInt(value, 10);

      if (int > MAX_SAFE_INT || int < MIN_SAFE_INT) {
        throw new Error(`overflow ${field_name} ${int}`);
      }

      return;
    }

    if (Long.isLong(value)) {
      // typeof value.toInt() is 'number'
      this.no_overflow53(value.toInt(), field_name);
      return;
    }

    throw new Error(`unsupported type ${field_name}: (${typeof value}) ${value}`);
  },

  // signed / unsigned whole numbers only
  no_overflow64(value, field_name = '', unsigned = false) {
    // https://github.com/dcodeIO/Long.js/issues/20
    if (Long.isLong(value)) {
      return;
    }

    // BigInteger#isBigInteger https://github.com/cryptocoinjs/bigi/issues/20
    if (value.t !== undefined && value.s !== undefined) {
      this.no_overflow64(value.toString(), field_name, unsigned);

      return;
    }

    if (typeof value === 'string') {
      // remove leading zeros, will cause a false positive
      value = value.replace(/^0+/, '');

      // remove trailing zeros
      while (/0$/.test(value)) {
        value = value.substring(0, value.length - 1);
      }

      if (/\.$/.test(value)) {
        // remove trailing dot
        value = value.substring(0, value.length - 1);
      }

      if (value === '') {
        value = '0';
      }

      let long_string = Long.fromString(value, unsigned).toString();

      if (long_string !== value.trim()) {
        throw new Error(`overflow ${field_name} ${value}`);
      }

      return;
    }

    if (typeof value === 'number') {
      if (value > MAX_SAFE_INT || value < MIN_SAFE_INT) {
        throw new Error(`overflow ${field_name} ${value}`);
      }

      return;
    }

    throw new Error(`unsupported type ${field_name}: (${typeof value}) ${value}`);
  }
};

export default _my;

import ByteBuffer from 'bytebuffer';

function toByteBuffer(type, object) {
  let b = new ByteBuffer(ByteBuffer.DEFAULT_CAPACITY, ByteBuffer.LITTLE_ENDIAN);
  type.appendByteBuffer(b, object);
  return b.copy(0, b.offset);
}

export default function (type) {
  return {
    fromHex(hex) {
      let b = ByteBuffer.fromHex(hex, ByteBuffer.LITTLE_ENDIAN);
      return type.fromByteBuffer(b);
    },

    toHex(object) {
      let b = toByteBuffer(type, object);
      return b.toHex();
    },

    fromBuffer(buffer) {
      let b = ByteBuffer.fromBinary(buffer.toString(), ByteBuffer.LITTLE_ENDIAN);
      return type.fromByteBuffer(b);
    },

    toBuffer(object) {
      return Buffer.from(toByteBuffer(type, object).toBinary(), 'binary');
    },

    fromBinary(string) {
      let b = ByteBuffer.fromBinary(string, ByteBuffer.LITTLE_ENDIAN);
      return type.fromByteBuffer(b);
    },

    toBinary(object) {
      return toByteBuffer(type, object).toBinary();
    }
  };
}

/** Exception nesting.  */
class ErrorWithCause {
  constructor(message, cause) {
    this.message = message;

    if (typeof cause !== 'undefined' && cause !== null ? cause.message : undefined) {
      this.message = `cause\t${cause.message}\t${this.message}`;
    }

    let stack = ''; // (new Error).stack

    if (typeof cause !== 'undefined' && cause !== null ? cause.stack : undefined) {
      stack = `caused by\n\t${cause.stack}\t${stack}`;
    }

    this.stack = `${this.message}\n${stack}`;
  }

  static throw(message, cause) {
    let msg = message;

    if (typeof cause !== 'undefined' && cause !== null ? cause.message : undefined) {
      msg += `\t cause: ${cause.message} `;
    }

    if (typeof cause !== 'undefined' && cause !== null ? cause.stack : undefined) {
      msg += `\n stack: ${cause.stack} `;
    }

    throw new Error(msg);
  }
}

export default ErrorWithCause;

